import './App.css';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import {useState, useEffect} from 'react';

function App() {
  let [inputvalue, setInputValue] = useState('')
  let [data, setData] = useState([])
  const displayInput = (event) =>{
    let changeval = event.target.value
    setInputValue(changeval)
    console.log(inputvalue)
  }
  useEffect((data)=>{
    let val = [...data,inputvalue]
    setData(val)
  },[inputvalue])

  return (
    <div className="App">
      <header className="App-header">
        <h1>THINGS TO DO</h1>
        <div style={{display:"block"}}>
        <TextField fullWidth id="outlined-basic" label="Add More" variant="outlined"   style={{alignContent:'center'}} onChange={displayInput} />
        <Button variant="contained">Add More</Button>
        {
          data.map(d1 => <li key="{d1}">{d1}</li>)
        }
        </div>
      </header>
    </div>
  );
}

export default App;
