import './App.css';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { Checkbox } from '@mui/material';
import {useState} from 'react';
import {ListItemButton } from '@mui/material';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';

function App() {
  let [inputvalue, setInputValue] = useState('')
  let remaining = [];
  let completed = [];
  let [allData, setData] = useState([])
  const displayInput = (event) =>{
    let changeval = event.target.value
    setInputValue(changeval)
    console.log(inputvalue)
  }
  const updateval = () =>{
    let imp = [...allData,inputvalue]
    remaining.push(inputvalue)
    setData(imp)
  }
  const checkedBoxClicked = (val) =>{
    completed.push(val);
    console.log(completed)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>THINGS TO DO</h1>
        <div style={{display:"block"}}>
        <TextField fullWidth id="outlined-basic" label="Add More" variant="outlined"   style={{alignContent:'center'}} onChange={displayInput} />
        </div>
        {
          allData.map(d1 => <ListItemButton component="a" href="#simple-list">
          <ul><Checkbox onClick={()=> checkedBoxClicked(d1)}/>{d1}</ul>
        </ListItemButton>)
        }
        <AppBar position="static" color="success">
            <Toolbar variant="small">
              <IconButton edge="start" color="success" aria-label="menu" sx={{ mr:5 }}>
              <Button variant="contained" onClick={updateval}>+</Button>
              <h1>|</h1>
              </IconButton>
              <Typography variant="h6" color="inherit" component="div" style={{marginRight:"56%"}}>
                {allData.length} Items Left
              </Typography>
              <Button variant="contained" style={{margin:"1%"}}>ALL</Button>
              <Button variant="contained" style={{margin:"1%"}}>ACTIVE</Button>
              <Button variant="contained" style={{margin:"1%"}}>COMPLETED</Button>
            </Toolbar>
        </AppBar>
      </header>
    </div>
  );
}

export default App;
