import './App.css';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import {useState} from 'react';

function App() {
  let [data,setData] = useState([])
  let [inputvalue, setInputValue] = useState('')
  const displayInput = (event) =>{
    setInputValue(event.target.value)
  }
  const addMore = (event) =>{
    console.log(event.target.value)
      let val = [...data];
      val.push(event.target.value)
      setData(val)
      console.log(data)
    }
  return (
    <div className="App">
      <header className="App-header">
        <h1>THINGS TO DO</h1>
        <div style={{display:"block"}}>
        <TextField fullWidth id="outlined-basic" label="Add More" variant="outlined"   style={{alignContent:'center'}} onChange={displayInput()} />
        <Button variant="contained" onClick={addMore(event)}>Add More</Button>
        {
          data.map(d1 => <li>{d1}</li>)
        }
        </div>
      </header>
    </div>
  );
}

export default App;
